def _svg(viewbox: tuple, size: Optional[Tuple[int, int]]) -> ET.Element:
    x, y, w, h = viewbox
    svg = ET.Element("svg", {
        "xmlns": "http://www.w3.org/2000/svg",
        "xmlns:xlink": "http://www.w3.org/1999/xlink",
        "version": "1.2",
        "baseProfile": "tiny",
        "viewBox": f"{x:d} {y:d} {w:d} {h:d}",
    })

    if size is not None:
        svg.set("width", str(size[0]))
        svg.set("height", str(size[1]))

    return svg


class SvgWrapper(str):
    def _repr_svg_(self):
        return self


def _attrs(attrs: Dict[str, Union[str, int, float, None]]) -> Dict[str, str]:
    return {k: str(v) for k, v in attrs.items() if v is not None}


def piece(piece: cchess.Piece, size: Optional[int] = None) -> str:
    """
    Renders the given :class:`cchess.Piece` as an SVG image.
    """
    svg = _svg((0, 0, SQUARE_SIZE, SQUARE_SIZE), (size, size))
    svg.append(ET.fromstring(PIECES[piece.symbol()]))
    return SvgWrapper(ET.tostring(svg).decode("utf-8"))


